import { Grid, Paper } from '@material-ui/core';
import React from 'react';
import { makeStyles } from '@material-ui/core/styles';

import { sections } from '../../src/directory.data';
import { CardHome } from './CardHome';

const useStyles = makeStyles({
  root: {
    paddingTop: 10,
  },
  paperThree: {
    minHeight: 300,
    transition: 'transform .5s',
    '&:hover': {
      cursor: 'pointer',
      transform: 'scale(1.5)',
    },
    // display: 'flex',
    // flexBasis: '100%',
  },

  paperTwo: {
    minHeight: 400,
    transition: 'transform .5s',
    '&:hover': {
      cursor: 'pointer',
      transform: 'scale(1.5)',
    },
    // display: 'flex',
    // flexBasis: '100%',
  },
});

export const HomePage = () => {
  const classes = useStyles(sections);
  return (
    <Grid container spacing={3} className={classes.root}>
      {sections.slice(0, 3).map((sec) => (
        <Grid key={sec.id} item sm={4} xs={12}>
          {/* <div style={{ overflow: 'hidden' }}>
            <Paper
              className={classes.paperThree}
              style={{
                background: `url(${sec.imageUrl}) center`,
                backgroundSize: 'cover',
              }}
            >
              <Paper>{sec.title}</Paper>
            </Paper>
          </div> */}
          <CardHome {...sec} />
        </Grid>
      ))}
      {sections.slice(3, 6).map((sec) => (
        <Grid key={sec.id} item sm={6} xs={12}>
          <div style={{ overflow: 'hidden' }}>
            <Paper
              className={classes.paperTwo}
              style={{
                background: `url(${sec.imageUrl}) center`,
                backgroundSize: 'cover',
              }}
            >
              {sec.title}
            </Paper>
          </div>
        </Grid>
      ))}
    </Grid>
  );
};
